import React from 'react';
import { Button, Grid, Paper } from '@mui/material';

interface SymbolKeyboardProps {
  onSymbolClick: (symbol: string) => void;
}

export const SymbolKeyboard: React.FC<SymbolKeyboardProps> = ({ onSymbolClick }) => {
  const symbols = [
    { label: 'dy/dx', value: 'dy/dx = ' },
    { label: 'd²y/dx²', value: 'd²y/dx² = ' },
    { label: '∫', value: '∫' },
    { label: '∂', value: '∂' },
    { label: 'sin', value: 'sin(' },
    { label: 'cos', value: 'cos(' },
    { label: 'e^', value: 'e^(' },
    { label: 'ln', value: 'ln(' },
    { label: '√', value: 'sqrt(' },
    { label: 'x', value: 'x' },
    { label: 'y', value: 'y' },
    { label: '=', value: ' = ' },
    { label: '+', value: ' + ' },
    { label: '-', value: ' - ' },
    { label: '×', value: ' * ' },
    { label: '÷', value: ' / ' },
    { label: '(', value: '(' },
    { label: ')', value: ')' },
    { label: '^', value: '^' },
    { label: 'π', value: 'pi' },
  ];

  return (
    <Paper elevation={2} sx={{ p: 1, mt: 2 }}>
      <Grid container spacing={1}>
        {symbols.map((symbol, index) => (
          <Grid item xs={3} sm={2} key={index}>
            <Button
              variant="outlined"
              fullWidth
              onClick={() => onSymbolClick(symbol.value)}
              sx={{
                minWidth: 0,
                p: 1,
                fontFamily: 'math',
                fontSize: '1rem'
              }}
            >
              {symbol.label}
            </Button>
          </Grid>
        ))}
      </Grid>
    </Paper>
  );
};
