import React, { useState } from 'react';
import { Box, TextField, Button, Grid, Paper } from '@mui/material';
import * as math from 'mathjs';

interface CalculatorState {
  input: string;
  result: string;
  history: string[];
}

interface DifferentialEquationPoint {
  x: number;
  y: number;
}

export const Calculator: React.FC = () => {
  const [state, setState] = useState<CalculatorState>({
    input: '',
    result: '',
    history: []
  });

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setState(prev => ({
      ...prev,
      input: event.target.value
    }));
  };

  const solveDifferentialEquation = (equation: string) => {
    try {
      // Simple numerical solution using Euler's method
      const h = 0.1; // step size
      const steps = 100;
      const results: DifferentialEquationPoint[] = [];
      let x = 0;
      let y = 1; // initial condition y(0) = 1

      // Convert equation from dy/dx format to a function
      const equationFunc = (x: number, y: number): number => {
        const expr = equation
          .replace('dy/dx', '')
          .replace('=', '')
          .trim();
        const result = math.evaluate(expr, { x, y });
        return typeof result === 'number' ? result : 0;
      };

      for (let i = 0; i <= steps; i++) {
        results.push({ x, y });
        const dy = equationFunc(x, y);
        y += h * dy;
        x += h;
      }

      setState(prev => ({
        ...prev,
        result: JSON.stringify(results, null, 2),
        history: [...prev.history, `${equation} solved numerically`]
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        result: `Error: ${error instanceof Error ? error.message : 'Invalid equation format'}`
      }));
    }
  };

  const handleSolve = () => {
    if (!state.input.trim()) {
      setState(prev => ({
        ...prev,
        result: 'Error: Please enter an equation'
      }));
      return;
    }

    if (state.input.toLowerCase().includes('dy/dx')) {
      solveDifferentialEquation(state.input);
    } else {
      try {
        const result = math.evaluate(state.input);
        setState(prev => ({
          ...prev,
          result: result.toString(),
          history: [...prev.history, `${state.input} = ${result}`]
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          result: `Error: ${error instanceof Error ? error.message : 'Invalid expression'}`
        }));
      }
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Paper elevation={3} sx={{ p: 2 }}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Enter equation"
              variant="outlined"
              value={state.input}
              onChange={handleInputChange}
              placeholder="Enter mathematical expression or differential equation (e.g., 'dy/dx = x^2' or '2+2')"
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  handleSolve();
                }
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              fullWidth
              variant="contained"
              color="primary"
              onClick={handleSolve}
            >
              Solve
            </Button>
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Result"
              value={state.result}
              InputProps={{ readOnly: true }}
            />
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
};
