import React, { useState } from 'react';
import { Box, TextField, Button, Grid, Paper } from '@mui/material';
import * as math from 'mathjs';
import { Solver } from 'odex';

interface CalculatorState {
  input: string;
  result: string;
  history: string[];
}

interface DifferentialEquationResult {
  x: number;
  y: number;
}

export const Calculator: React.FC = () => {
  const [state, setState] = useState<CalculatorState>({
    input: '',
    result: '',
    history: []
  });

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setState(prev => ({
      ...prev,
      input: event.target.value
    }));
  };

  const solveDifferentialEquation = async (equation: string) => {
    try {
      // Basic differential equation solver using odex
      const solver = new Solver((x: number, y: number[]) => {
        try {
          // Parse and evaluate the equation using mathjs
          const scope = { x, y: y[0] };
          const derivative = math.evaluate(equation, scope);
          return [Number(derivative)];
        } catch (error) {
          console.error('Error evaluating equation:', error);
          throw new Error('Invalid differential equation format');
        }
      }, 1);

      // Solve from x=0 to x=10 with initial condition y(0)=1
      const solution = solver.integrate(0, [1]);
      const points = Array.from({ length: 11 }, (_, i) => i);
      const results: DifferentialEquationResult[] = points.map(x => ({
        x,
        y: solution(x)[0]
      }));

      setState(prev => ({
        ...prev,
        result: JSON.stringify(results, null, 2)
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        result: `Error: ${error instanceof Error ? error.message : 'Unknown error occurred'}`
      }));
    }
  };

  const handleSolve = () => {
    if (!state.input.trim()) {
      setState(prev => ({
        ...prev,
        result: 'Error: Please enter an equation'
      }));
      return;
    }

    if (state.input.includes('d/dx') || state.input.includes('dy/dx')) {
      solveDifferentialEquation(state.input);
    } else {
      try {
        const result = math.evaluate(state.input);
        setState(prev => ({
          ...prev,
          result: result.toString(),
          history: [...prev.history, `${state.input} = ${result}`]
        }));
      } catch (error) {
        setState(prev => ({
          ...prev,
          result: `Error: ${error instanceof Error ? error.message : 'Invalid expression'}`
        }));
      }
    }
  };

  return (
    <Box sx={{ p: 3 }}>
      <Paper elevation={3} sx={{ p: 2 }}>
        <Grid container spacing={2}>
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Enter equation"
              variant="outlined"
              value={state.input}
              onChange={handleInputChange}
              placeholder="Enter mathematical expression or differential equation (e.g., 'd/dx(x^2)' or '2+2')"
              onKeyPress={(e) => {
                if (e.key === 'Enter') {
                  handleSolve();
                }
              }}
            />
          </Grid>
          <Grid item xs={12}>
            <Button
              fullWidth
              variant="contained"
              color="primary"
              onClick={handleSolve}
            >
              Solve
            </Button>
          </Grid>
          <Grid item xs={12}>
            <TextField
              fullWidth
              multiline
              rows={4}
              variant="outlined"
              label="Result"
              value={state.result}
              InputProps={{ readOnly: true }}
            />
          </Grid>
        </Grid>
      </Paper>
    </Box>
  );
};
