import React, { useState, useRef } from 'react';
import { Container, Form, Button, Card, Row, Col } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import * as math from 'mathjs';
import { SymbolKeyboard } from './SymbolKeyboard';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

interface CalculatorState {
  input: string;
  result: string;
  history: string[];
}

interface DifferentialEquationPoint {
  x: number;
  y: number;
}

interface GraphDataPoint {
  x: number;
  y: number | null;
}

export const Calculator: React.FC = () => {
  const [state, setState] = useState<CalculatorState>({
    input: '',
    result: '',
    history: []
  });
  const inputRef = useRef<HTMLInputElement>(null);

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setState(prev => ({
      ...prev,
      input: event.target.value
    }));
  };

  const evaluateExpression = (expr: string, variables: Record<string, number>): number => {
    try {
      return math.evaluate(expr, { ...variables, e: math.e, pi: math.pi });
    } catch (err) {
      throw new Error(`Invalid expression: ${expr}`);
    }
  };

  const numericalIntegration = (f: (x: number) => number, a: number, b: number): number => {
    const n = 1000; // number of intervals
    const h = (b - a) / n;
    
    let sum = f(a) + f(b);
    for (let i = 1; i < n; i++) {
      const x = a + i * h;
      sum += 2 * f(x);
    }
    for (let i = 1; i < n; i++) {
      const x = a + (i - 0.5) * h;
      sum += 4 * f(x);
    }
    return (h / 6) * sum;
  };

  const parseIntegral = (equation: string): { expr: string, vars: string[] } => {
    const integralRegex = /∫\s*([^d]*)\s*d([a-z])/gi;
    const matches = Array.from(equation.matchAll(integralRegex));
    
    if (matches.length === 0) {
      throw new Error('No valid integral found');
    }

    const expr = matches[0][1].trim();
    const variable = matches[0][2].toLowerCase();
    const remainingExpr = equation.replace(matches[0][0], expr);

    if (matches.length > 1) {
      const { expr: nestedExpr, vars: nestedVars } = parseIntegral(remainingExpr);
      return {
        expr: nestedExpr,
        vars: [variable, ...nestedVars]
      };
    }

    return { expr, vars: [variable] };
  };

  const solveNestedIntegral = (expr: string, vars: string[], depth: number = 0): number => {
    if (depth >= vars.length) {
      return evaluateExpression(expr, {});
    }

    const currentVar = vars[depth];
    const a = 0; // lower bound
    const b = 1; // upper bound

    const f = (x: number) => {
      const result = solveNestedIntegral(expr, vars, depth + 1);
      return evaluateExpression(expr, { [currentVar]: x });
    };

    return numericalIntegration(f, a, b);
  };

  const solveIntegral = (equation: string) => {
    try {
      const parts = equation.split('*').map(part => part.trim());
      const results = parts.map(part => {
        const { expr, vars } = parseIntegral(part);
        const result = solveNestedIntegral(expr, vars);
        return { part, result, order: vars.length };
      });

      const finalResult = results.reduce((acc, curr) => acc * curr.result, 1);

      setState(prev => ({
        ...prev,
        result: `Result = ${finalResult.toFixed(6)}\n\nDetails:\n${results.map(({ part, result, order }) => 
          `${part} (${order}-fold integral) = ${result.toFixed(6)}`
        ).join('\n')}`,
        history: [...prev.history, `Solved multiple integrals: ${equation}`]
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        result: `Error: ${error instanceof Error ? error.message : 'Invalid integral format'}`
      }));
    }
  };

  const solveDifferentialEquation = (equation: string, returnPoints: boolean = false) => {
    try {
      const h = 0.1;
      const steps = 100;
      const results: DifferentialEquationPoint[] = [];
      let x = 0;
      let y = 1;

      const equationFunc = (x: number, y: number): number => {
        const expr = equation
          .replace('dy/dx', '')
          .replace('=', '')
          .trim();
        const result = math.evaluate(expr, { x, y });
        return typeof result === 'number' ? result : 0;
      };

      for (let i = 0; i <= steps; i++) {
        results.push({ x, y });
        const dy = equationFunc(x, y);
        y += h * dy;
        x += h;
      }

      if (returnPoints) {
        return results;
      }

      setState(prev => ({
        ...prev,
        result: JSON.stringify(results, null, 2),
        history: [...prev.history, `${equation} solved numerically`]
      }));
      return [];
    } catch (error) {
      if (!returnPoints) {
        setState(prev => ({
          ...prev,
          result: `Error: ${error instanceof Error ? error.message : 'Invalid equation format'}`
        }));
      }
      return [];
    }
  };

  const handleSolve = () => {
    if (!state.input.trim()) {
      setState(prev => ({
        ...prev,
        result: 'Error: Please enter an equation'
      }));
      return;
    }

    try {
      if (state.input.includes('∫')) {
        solveIntegral(state.input);
      } else if (state.input.toLowerCase().includes('dy/dx')) {
        solveDifferentialEquation(state.input);
      } else {
        const result = math.evaluate(state.input);
        const formattedResult = Math.abs(result) < 1e-10 ? 0 : result;
        setState(prev => ({
          ...prev,
          result: formattedResult.toString(),
          history: [...prev.history, `${state.input} = ${formattedResult}`]
        }));
      }
    } catch (error) {
      setState(prev => ({
        ...prev,
        result: `Error: ${error instanceof Error ? error.message : 'Invalid expression'}`
      }));
    }
  };

  const handleSymbolClick = (symbol: string) => {
    const input = inputRef.current;
    if (input) {
      const start = input.selectionStart || 0;
      const end = input.selectionEnd || 0;
      const newValue = state.input.substring(0, start) + symbol + state.input.substring(end);
      setState(prev => ({
        ...prev,
        input: newValue
      }));
      setTimeout(() => {
        const newPosition = start + symbol.length;
        input.setSelectionRange(newPosition, newPosition);
        input.focus();
      }, 0);
    }
  };

  const generateGraphData = () => {
    try {
      const xValues: number[] = [];
      const yValues: (number | null)[] = [];
      
      // Generate more points for smoother curves
      for (let x = -10; x <= 10; x += 0.1) {
        xValues.push(x);
        let y: number | null = null;
        
        if (state.input.includes('∫')) {
          const { expr } = parseIntegral(state.input);
          y = evaluateExpression(expr, { x });
        } else if (state.input.toLowerCase().includes('dy/dx')) {
          const results = solveDifferentialEquation(state.input, true);
          const point = results.find(p => Math.abs(p.x - x) < 0.1);
          y = point ? point.y : null;
        } else {
          try {
            const processedInput = state.input
              .replace(/sin\(/g, 'math.sin(')
              .replace(/cos\(/g, 'math.cos(')
              .replace(/tan\(/g, 'math.tan(')
              .replace(/cot\(/g, 'math.cot(')
              .replace(/sec\(/g, 'math.sec(')
              .replace(/csc\(/g, 'math.csc(');
            y = math.evaluate(processedInput, { x, math });
          } catch {
            y = null;
          }
        }
        
        if (y !== null && !isNaN(y) && isFinite(y)) {
          yValues.push(y);
        } else {
          yValues.push(null);
        }
      }

      return {
        labels: xValues,
        datasets: [
          {
            label: state.input,
            data: yValues.map((y, i) => ({ x: xValues[i], y })),
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
            pointRadius: 0,
            spanGaps: true
          }
        ]
      };
    } catch {
      return null;
    }
  };

  const graphOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'linear' as const,
        position: 'center' as const,
        title: {
          display: true,
          text: 'x'
        },
        ticks: {
          stepSize: 1
        },
        grid: {
          display: true,
          color: '#ddd'
        }
      },
      y: {
        type: 'linear' as const,
        position: 'center' as const,
        title: {
          display: true,
          text: 'y'
        },
        ticks: {
          stepSize: 1
        },
        grid: {
          display: true,
          color: '#ddd'
        }
      }
    },
    plugins: {
      legend: {
        display: true,
        position: 'top' as const
      }
    }
  };

  return (
    <Container fluid className="vh-100 p-0">
      <Row className="h-100 g-0">
        <Col md={6} className="h-100 p-0">
          <Card className="h-100 rounded-0 border-0">
            <Card.Body className="d-flex flex-column p-3">
              <Form className="flex-grow-1 d-flex flex-column">
                <Form.Group className="mb-3">
                  <Form.Control
                    type="text"
                    placeholder="Enter mathematical expression or differential equation"
                    value={state.input}
                    onChange={handleInputChange}
                    onKeyPress={(e) => {
                      if (e.key === 'Enter') {
                        handleSolve();
                      }
                    }}
                    ref={inputRef}
                    className="form-control-lg"
                  />
                </Form.Group>

                <div className="mb-3 border rounded p-2 bg-light">
                  <div className="d-grid">
                    <Row className="g-1">
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('dy/dx')}>dy/dx</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('∫')}>∫</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('sin(')}>sin</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('cos(')}>cos</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('E^')}>E^</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('ln(')}>ln</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('√')}>√</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('x')}>x</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('y')}>y</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('=')}>=</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('+')}>+</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('-')}>-</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('×')}>×</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('÷')}>÷</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('(')}>(</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick(')')}>)</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('^')}>^</Button></Col>
                      <Col xs={4}><Button variant="outline-primary" className="w-100 py-2" onClick={() => handleSymbolClick('π')}>π</Button></Col>
                    </Row>
                  </div>
                </div>

                <div className="d-grid mb-3">
                  <Button
                    variant="primary"
                    size="lg"
                    onClick={handleSolve}
                    className="py-2"
                  >
                    Solve
                  </Button>
                </div>

                <Form.Group className="flex-grow-1">
                  <Form.Control
                    as="textarea"
                    className="h-100 bg-light border-0"
                    value={state.result}
                    readOnly
                    style={{ resize: 'none' }}
                  />
                </Form.Group>
              </Form>
            </Card.Body>
          </Card>
        </Col>
        <Col md={6} className="h-100 p-0">
          <Card className="h-100 rounded-0 border-0">
            <Card.Body className="p-3">
              <div className="h-100">
                {state.input && (
                  <Line
                    data={generateGraphData() || { labels: [], datasets: [] }}
                    options={graphOptions}
                  />
                )}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};
